import React, { Component } from 'react'
import Athlete from "./athlete";

export default class AthleteInfo extends Component {
    state = {
        athletes: [
            {
                id: 1,
                firstName: "Pekka",
                lastName: "Routalempi",
            },
            {
                id: 2,
                firstName: "Rauno",
                lastName: "Repomies",
            },
        ],
    }
    
    deleteAthlete = (id) => {
        const {athletes} = this.state;

        const newAthletes = athletes.filter(
            (athlete) => athlete.id !== id
        );

        this.setState({
            athletes: newAthletes,
        });
    }

    render() {
        const {athletes} = this.state;
        return (
            <div>
                {athletes.map((athlete) =>(
                    <Athlete 
                    key={athlete.id}
                    firstName={athlete.firstName}
                    lastName={athlete.lastName}
                    deleteClickHandler={this.deleteAthlete.bind(
                        this,
                        athlete.id
                    )}/>
                ))}
            </div>
        )
    }
}
