import React, {Component} from "react";

const Context = React.createContext();

const reducer = (state, action) => {
    switch (action.type) {
        case "DELETE_ATHLETE":
            return {
                ...state,
                athlete: state.athletes.filter(
                    (athlete) => athlete.id !== athlete.payload
                ),
            };
        case "ADD_ATHLETE":
            return {
                ...state,
                athlete: [action.payload,
                            ...state.athletes],
            };
        default:
            return state;
    }
}

export class Provider extends Component {
    state = {
        athletes: [
            {
                id: 1,
                firstName: "Pekka",
                lastName: "Routalempi",
            },
            {
                id: 2,
                firstName: "Rauno",
                lastName: "Repomies",
            },
        ],
        dispatch: (action) => this.setState((state) => reducer(state,action))
    }

    render () {
        return (
            <Context.Provider value={this.state}>
                {this.props.children}
            </Context.Provider>
        );
    }
}

export const Consumer = Context.Consumer;
