const mysql = require("mysql");

const con = mysql.createConnection({
    host: "localhost",
    user: "root",
    password: "test",
    database: "athletes",multipleStatements: true
});

function connectionStart() {
    con.connect((err) => {
        if (err) {
            console.log("Error connecting to DB.");
            return;
        }
        console.log("Connection established.");
    });
}

connectionStart;

function connectionEnd() {
    con.end((err) => {
        if (err) {
            console.log("Error in terminating the connection.");
            return;
        }
        console.log("Connection terminated.");
    })
}

//Injection preventer.
const userSubmittedVariable = "1";

con.query(
    `SELECT * FROM athleteinformation WHERE id = ${mysql.escape(userSubmittedVariable)}`,
    (err, rows) => {
        if (err) throw err;
        console.log(rows[0]);
    }
);


const path = require("path");
const express = require("express");
const bodyParser = require("body-parser");
const app = express().use(bodyParser.json());

//Redirect to the index.html.
app.get("/", (req,res) => {
    res.sendFile(path.join(__dirname, "index.html"));
});


//Get all athletes.
app.get("/athletes", (req, res) => {
    con.query(
        "CALL get_all_athletes();", (err, result) => {
            if (err) throw err;

            return res.status(200).json(result[0]);
        }
    )
});

//Get an athlete.
app.get("/athletes/:firstName-:lastName", (req, res) => {
    const firstname = String(req.params.firstName);
    const lastname = String(req.params.lastName);

    con.query(
        "CALL get_athlete(?, ?)", [firstname, lastname], (err, result) => {
            if (err) throw err;

            const athlete = result[0].find((athlete) => (athlete.firstName === firstname && athlete.lastName === lastname));

            return res.json(athlete ? athlete : {messae: "Athlete not found."});
        }
    )
}
);

//Add an athlete
app.post("/addathlete/", (req, res) => {

    //NÄMÄ TIEDOT LÄHETETÄÄN JSON-TIEDOSTONA SERVERILLE!
    //Place the information from the body of the request to these variables.
    const {id, firstName, lastName, callName, yearOfBirth, weight, imageLink, sport, accomplishments} = req.body;

    
    con.query(
        "CALL insert_athlete(@?, ?, ?, ?, ?, ?, ?, ?, ?);", [id, firstName, lastName, callName, yearOfBirth, weight, imageLink, sport, accomplishments], (err, result) => {
            if (err) throw err;

            console.log(result);
        }
    )

    return res.send({
        message: "Athlete added.",
        data: `Data on ${id + " " + firstName + " " + lastName + " " + callName + " " + yearOfBirth + " " + weight + " " + imageLink + " " + sport + " " + accomplishments.feat}`
    })
}
);

//Update an athlete
app.put("/updateathlete/", (req, res) => {
    const {id, newFirstName, newLastName, newCallName, newyob, newWeight, newImageLink, newSport, newAccomplishments} = req.body;

    con.query(
        "CALL update_athlete(?,?,?,?,?,?,?,?,?);", [id, newFirstName, newLastName, newCallName, newyob, newWeight, newImageLink, newSport, newAccomplishments], (err, result) => {
            if (err) throw err;

            console.log(result);
        }
    )

    return res.send({
        message: "Athlete updated",
        data: `Data on ${id + " " + newFirstName + " " + newLastName + " " + newCallName + " " + newyob + " " + newImageLink + " " + newSport + " " + newAccomplishments}`
    })
});

//Delete an athlete
app.delete("/deleteathlete/", (req, res) => {
    const {id, firstName, lastName} = req.body;

    con.query(
        "CALL delete_athlete(?, ?, ?)", [id, firstName, lastName], (err, result) => {
            if (err) throw err;
        }
    )

    return res.send({
        message: "Athlete deleted.",
        data: `Data on ${id + " " + firstName + " " + lastName}`
    })
});

app.listen(3000, () => {
    console.log("Server listening at port 3000");
});

// Add headers
app.use(function (req, res, next) {
    // Website you wish to allow to connect
    res.setHeader("Access-Control-Allow-Origin", "*");
  
    // Request methods you wish to allow
    res.setHeader(
      "Access-Control-Allow-Methods",
      "GET, POST, OPTIONS, PUT, PATCH, DELETE"
    );
  
    // Request headers you wish to allow
    res.setHeader(
      "Access-Control-Allow-Headers",
      "X-Requested-With, Content-type"
    );
  
    // Set to true if you need the website to include cookies in the requests sent
    // to the API (e.g. in case you use sessions)
    res.setHeader("Access-Control-Allow-Credentials", true);
  
    res.getHeaders();
  
    // Pass to next layer of middleware
    next();
  });
