import React, { Component } from "react";
import PropTypes from 'prop-types'
import "./athleteStyle.css";
import {Consumer} from "../context";
import axios from "axios";

export default class Athlete extends Component {
    state = {
        showAthlete: false,
    }

    onDeleteClick = async (key, dispatch) => {
        const {id, firstName, lastName} = this.state;
        const deletedAthlete = {
            id: id,
            firstName: firstName,
            lastName: lastName,
        }

        const res = await axios.delete(`http://localhost:3000/deleteathlete/`, deletedAthlete);

        dispatch({type: "DELETE_ATHLETE", payload: res.data});
        console.log(res);
    }

    onShowClick = (e) => {
        this.setState({showAthlete: !this.state.showAthlete});
        console.log(this.state);
    }

    render() {
        const {key, firstName, lastName, callName, yearOfBirth, weight, sport, accomplishments} = this.props.athleteInfo;
        const {showAthlete} = this.state;

        return (
            <Consumer>
                {(value) => {
                    const {dispatch} = value;
                    return (
                        <div className="card card-body mb-3">
                            <h4>
                                {firstName}{" "}
                                <button className="button" onClick={this.onShowClick.bind(this)}>
                                    +
                                </button>
                                <button className="button_right" onClick={this.onDeleteClick.bind(this, key, dispatch)}>
                                    Delete
                                </button>
                            </h4>
                            
                            {showAthlete ? (
                                <ul className="list-group">
                                    <li className="list-group-item" key={key + lastName}>Lastname: {lastName}</li>
                                    <li className="list-group-item" key ={key + callName}>Callname: {callName}</li>
                                    <li className="list-group-item" key={key + yearOfBirth}>Year of birth: {yearOfBirth}</li>
                                    <li className="list-group-item" key={key + weight}>Weight: {weight}</li>
                                    <li className="list-group-item" key={key + sport}>Sport: {sport}</li>
                                    <li className="list-group-item" key={key + accomplishments}>Accomplishments: {accomplishments}</li>
                                </ul>
                            ) : null}
                        </div>
                    );
                }}
            </Consumer>
        );
    }
}

Athlete.propTypes = {

    athleteInfo: PropTypes.object.isRequired,

    //key: PropTypes.number.isRequired,
    //firstName: PropTypes.string.isRequired,
    //lastName: PropTypes.string.isRequired,
}
