import React, { Component } from 'react'
import {Consumer} from "../context";
import uuid from 'uuid';
import axios from "axios";

export default class AddAthlete extends Component {
    state = {
            firstName: "",
            lastName: "",
    }

    onSubmit = async (dispatch, e) => {
        e.preventDefault();
        
        const {firstName, lastName, callName, yearOfBirth, weight, imageLink, sport, accomplishments} = this.state;
        const newAthlete = {
            id: uuid(),
            firstName: firstName,
            lastName: lastName,
            callName: callName,
            yearOfBirth: yearOfBirth,
            weight: weight,
            imageLink: imageLink,
            sport: sport,
            accomplishments: accomplishments,
        }

        dispatch({type: "ADD_ATHLETE", payload: newAthlete});

        await axios
            .push(`http://localhost:3000/addathlete/${id-firstName-lastName-callName-yearOfBirth-weight-imageLink-sport-accomplishments}`)
            .then((res) => dispatch({type: "DELETE_ATHLETE", payload: id-firstName-lastName-callName-yearOfBirth-weight-imageLink-sport-accomplishments}));

        this.props.history.push("/");
    }

    onChange = (e) => this.setState({[e.target.name]: e.target.value});

    render() {
        const {firstName, lastName} = this.state;

        return (

            <Consumer>
                {(value) => {
                    const {dispatch} = value;
                    return (
                        <div className="card mb-3">
                            <div className="card-header">Add athlete</div>
                            <div className="card-body">
                                <form onSubmit={this.onSubmit.bind(this, dispatch)}>
                                    <div className="form-group">
                                        <label htmlFor="firstName">Firstname</label>
                                        <input
                                            type="text"
                                            name="firstName"
                                            className="form-control form-control-lg"
                                            placeholder="Input firstname..."
                                            value={firstName}
                                            onChange={this.onChange}
                                        />
                                    </div>
                                    <div className="form-group">
                                        <label htmlFor="lastName">Lastname</label>
                                        <input
                                            type="text"
                                            name="lastName"
                                            className="form-control form-control-lg"
                                            placeholder="Input lastname..."
                                            value={lastName}
                                            onChange={this.onChange}
                                        />
                                    </div>
                                    <input
                                        type="submit"
                                        value="Add athlete"
                                        className="btn btn-light btn-block"
                                    />
                                </form>
                            </div>
                        </div>
                    );
                }}
            </Consumer>
        );
    }
}
