import React, { Component } from "react";
import PropTypes from 'prop-types'
import "./athlete.css";

export default class Athlete extends Component {
    state = {
        showAthlete: false,
    }

    onDeleteClick = () => {
        this.props.deleteClickHandler();
    }

    onShowClick = (e) => {
        this.setState({showAthlete: !this.state.showAthlete});
        console.log(this.state);
    }

    render() {
        const {firstName, lastName} = this.props;
        const {showAthlete} = this.state;

        return (
            <div className="card card-body mb-3">
                <h4>
                    {firstName}{" "}
                    <button className="button" onClick={this.onShowClick.bind(this)}>
                        +
                    </button>
                    <button className="button_right" onClick={this.onDeleteClick}>
                        Delete
                    </button>
                </h4>
                
                {showAthlete ? (
                    <ul className="list-group">
                        <li className="list-group-item">Lastname: {lastName}</li>
                    </ul>
                ) : null}
            </div>
        );
    }
}

Athlete.propTypes = {
    firstName: PropTypes.string.isRequired,
    lastName: PropTypes.string.isRequired,
    deleteClickHandler: PropTypes.func.isRequired,
}
