import React, { Component } from 'react'
import Athlete from "./Athlete";
import {Consumer} from "../context";

export default class AthleteInfo extends Component {
    state = {}
    
    deleteAthlete = (id) => {
        /*
        const {athletes} = this.state;

        const newAthletes = athletes.filter(
            (athlete) => athlete.id !== id
        );

        this.setState({
            athletes: newAthletes,
        });
        */
    }
    

    render() {
        
        return (
            <Consumer>
                {(value) => {
                    const {athletes} = this.state;
                    return (
                        <div>
                            <h1 className="display-4 mb-2">
                                <span className="text-danger">Athlete information</span>
                            </h1>
                            {athletes.map((athlete) =>(
                                <Athlete 
                                    key={athlete.id}
                                    athleteInfo={athlete}
                                    deleteClickHandler={this.deleteAthlete.bind(
                                        this,
                                        athlete.id
                                    )}
                                />
                            ))}
                        </div>
                    );
                }}
            </Consumer>
        );
    }
}
