import React, {Component} from "react";
import axios from "axios";

const Context = React.createContext();

//DIA 65!!!!!!!!!!!!!!!!!!!!

const reducer = (state, action) => {

    switch (action.type) {
        case "DELETE_ATHLETE":
            return {
                ...state,
                athlete: state.athletes.filter(
                    (athlete) => athlete.id !== athlete.payload
                ),
            };
        case "ADD_ATHLETE":
            return {
                ...state,
                athletes: [...state.athletes, action.payload],
            };
        default:
            return state;
    }
}

export class Provider extends Component {
    state = {
        athletes: [],
        dispatch: (action) => {
            this.setState((state) => reducer(state, action))
            console.log("STATE");
            console.log(this.state.athletes);
        }
    }


    async componentDidMount() {
        await axios.get("http://localhost:3000/athletes")
        .then((res) => {
            this.setState({athletes: res.data})
        });
    }

    render () {
        return (
            <Context.Provider value={this.state}>
                {this.props.children}
            </Context.Provider>
        );
    }
}

export const Consumer = Context.Consumer;
